import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js'; 
import '@fortawesome/fontawesome-free/css/all.min.css';
import './App.css';

// --- DATA ---
const servicesData = [
  {
    icon: "fas fa-camera",
    title: "Portrait Photography",
    desc: "Professional headshots, family portraits, and creative modeling shoots with expert lighting."
  },
  {
    icon: "fas fa-heart",
    title: "Wedding & Events",
    desc: "Full-day coverage for your special day. Capturing candid moments and cinematic highlights."
  },
  {
    icon: "fas fa-wand-magic-sparkles",
    title: "Photo Retouching",
    desc: "High-end editing and color grading to bring out the best in every image."
  }
];

const portfolioImages = [
  "https://images.unsplash.com/photo-1511895426328-dc8714191300?auto=format&fit=crop&w=600&q=80",
  "https://images.unsplash.com/photo-1531746020798-e6953c6e8e04?auto=format&fit=crop&w=600&q=80",
  "https://images.unsplash.com/photo-1469334031218-e382a71b716b?auto=format&fit=crop&w=600&q=80",
  "https://images.unsplash.com/photo-1542038784456-1ea8e935640e?auto=format&fit=crop&w=600&q=80",
  "https://images.unsplash.com/photo-1551316679-9c6ae9dec224?auto=format&fit=crop&w=600&q=80",
  "https://images.unsplash.com/photo-1492691527719-9d1e07e534b4?auto=format&fit=crop&w=600&q=80"
];

// --- NEW COMPONENT: LOGIN PAGE ---
const LoginPage = ({ onBack }) => {
  const [role, setRole] = useState('user'); // State to toggle between User and Admin

  return (
    <div className="d-flex align-items-center justify-content-center min-vh-100 bg-light" style={{paddingTop: '80px'}}>
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-6 col-lg-4">
            <div className="card shadow-lg border-0 rounded-3">
              <div className="card-body p-5">

                {/* Header with Toggle */}
                <div className="text-center mb-4">
                  <h3 className="fw-bold mb-3">{role === 'user' ? 'Client Login' : 'Admin Portal'}</h3>
                  <div className="btn-group w-100" role="group">
                    <button 
                      type="button" 
                      className={`btn ${role === 'user' ? 'btn-primary' : 'btn-outline-primary'}`}
                      onClick={() => setRole('user')}
                    >
                      User
                    </button>
                    <button 
                      type="button" 
                      className={`btn ${role === 'admin' ? 'btn-dark' : 'btn-outline-dark'}`}
                      onClick={() => setRole('admin')}
                    >
                      Admin
                    </button>
                  </div>
                </div>

                {/* The Form */}
                <form>
                  <div className="mb-3">
                    <label className="form-label">Email Address</label>
                    <div className="input-group">
                      <span className="input-group-text"><i className="fas fa-envelope"></i></span>
                      <input type="email" className="form-control" placeholder="name@example.com" />
                    </div>
                  </div>
                  <div className="mb-4">
                    <label className="form-label">Password</label>
                    <div className="input-group">
                      <span className="input-group-text"><i className="fas fa-lock"></i></span>
                      <input type="password" className="form-control" placeholder="********" />
                    </div>
                  </div>
                  <button type="submit" className={`btn w-100 mb-3 ${role === 'user' ? 'btn-primary' : 'btn-dark'}`}>
                    Login as {role === 'user' ? 'User' : 'Admin'}
                  </button>
                </form>

                <div className="text-center">
                  <a href="#" className="text-decoration-none small text-muted me-2">Forgot Password?</a>
                  <span className="text-muted">|</span>
                  {/* Button to go back to Home */}
                  <button onClick={onBack} className="btn btn-link text-decoration-none small">Back to Home</button>
                </div>

              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- EXISTING COMPONENTS (Unchanged) ---

const Navbar = ({ currentView, setView }) => (
  <nav className="navbar navbar-expand-lg navbar-dark fixed-top">
    <div className="container">
      {/* Clicking Brand returns to Home */}
      <a className="navbar-brand text-uppercase fw-bold" href="#" onClick={(e) => { e.preventDefault(); setView('home'); }}>
        <i className="fas fa-camera-retro me-2 text-gold"></i>E-Imi
      </a>
      <button 
        className="navbar-toggler" 
        type="button" 
        data-bs-toggle="collapse" 
        data-bs-target="#navbarNav"
      >
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className="collapse navbar-collapse" id="navbarNav">
        <ul className="navbar-nav ms-auto">
          {/* If we are on Home view, show all links */}
          {currentView === 'home' && ['Home', 'About', 'Services', 'Portfolio', 'Contact'].map((item) => (
            <li className="nav-item" key={item}>
              <a className="nav-link" href={`#${item.toLowerCase()}`}>{item}</a>
            </li>
          ))}

          {/* The Login Button (Always visible) */}
          <li className="nav-item ms-lg-3">
            <button 
              className={`btn btn-sm px-3 rounded-pill mt-1 ${currentView === 'login' ? 'btn-light text-dark' : 'btn-outline-light'}`}
              onClick={() => setView(currentView === 'home' ? 'login' : 'home')}
            >
              <i className="fas fa-user me-2"></i>
              {currentView === 'home' ? 'Login' : 'Home'}
            </button>
          </li>
        </ul>
      </div>
    </div>
  </nav>
);

const Hero = () => (
  <header id="home" className="hero-section text-center text-white">
    <div className="container">
      <div className="row">
        <div className="col-lg-8 mx-auto">
          <h1 className="display-2 fw-bold mb-4">Capture the Moment</h1>
          <p className="lead mb-5 text-light opacity-75">
            Professional photography services specializing in portraits, weddings, and landscapes. 
            Let us tell your story through the lens.
          </p>
          <a href="#portfolio" className="btn btn-outline-light btn-lg px-5 rounded-pill">View My Work</a>
        </div>
      </div>
    </div>
  </header>
);

const About = () => (
  <section id="about" className="section-padding">
    <div className="container">
      <div className="row align-items-center">
        <div className="col-lg-6 mb-4 mb-lg-0">
          <img 
            src="https://images.unsplash.com/photo-1492691527719-9d1e07e534b4?q=80&w=2071&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" 
            alt="Photographer" 
            className="img-fluid shadow-lg about-img" 
          />
        </div>
        <div className="col-lg-6 ps-lg-5">
          <h6 className="text-gold text-uppercase fw-bold mb-3">About Me</h6> 
          <h2 className="mb-4">Visual Storyteller & Editor</h2> 
          <p className="text-muted">
            I don't just take pictures; I create art. With over 5 years of experience in digital photography 
            and high-end photo editing, I strive to capture the raw emotion of every moment.
          </p>
          <p className="text-muted">
            Whether it's the golden hour light or a candid smile, my goal is to deliver memories that last a lifetime.
          </p>
          <a href="#contact" className="btn btn-dark mt-3 rounded-pill px-4">Get in Touch</a>
        </div>
      </div>
    </div>
  </section>
);

const Services = () => (
  <section id="services" className="section-padding bg-light">
    <div className="container">
      <div className="text-center mb-5">
        <h6 className="text-gold text-uppercase fw-bold">What I Do</h6>
        <h2 className="mb-3">My Services</h2>
      </div>
      <div className="row g-4">
        {servicesData.map((service, index) => (
          <div className="col-lg-4 col-md-6" key={index}>
            <div className="service-card p-5 bg-white rounded shadow-sm text-center">
              <div className="icon-box mb-4 text-gold">
                <i className={`${service.icon} fa-3x`}></i>
              </div>
              <h4>{service.title}</h4>
              <p className="text-muted mt-3">{service.desc}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  </section>
);

const Portfolio = () => (
  <section id="portfolio" className="section-padding">
    <div className="container">
      <div className="text-center mb-5">
        <h2 className="mb-3">Latest Works</h2>
        <p className="text-muted">A glimpse into my recent projects.</p>
      </div>
      <div className="row g-3">
        {portfolioImages.map((imgUrl, index) => (
          <div className="col-md-4 col-sm-6" key={index}>
            <img 
              src={imgUrl} 
              className="img-fluid rounded gallery-img" 
              alt={`Portfolio ${index + 1}`} 
            />
          </div>
        ))}
      </div>
    </div>
  </section>
);

const Contact = () => (
  <section id="contact" className="section-padding bg-dark text-white">
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-lg-8 text-center">
          <h2 className="mb-4">Let's Work Together</h2>
          <p className="mb-5 text-white-50">Ready to book a shoot? Send me a message and let's discuss your vision.</p>
        </div>
      </div>
      <div className="row justify-content-center">
        <div className="col-lg-8">
          <form>
            <div className="row g-3">
              <div className="col-md-6">
                <input type="text" className="form-control" placeholder="Your Name" required />
              </div>
              <div className="col-md-6">
                <input type="email" className="form-control" placeholder="Your Email" required />
              </div>
              <div className="col-12">
                <textarea className="form-control" rows="5" placeholder="Message or Project Details" required></textarea>
              </div>
              <div className="col-12 text-center mt-4">
                <button type="submit" className="btn btn-primary px-5 py-3 fw-bold">Send Message</button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  </section>
);

const Footer = () => (
  <footer className="py-4 bg-black text-center text-white-50">
    <div className="container">
      <p className="m-0">&copy; 2026 Lumina Lens Photography. All Rights Reserved.</p>
      <div className="social-icons mt-3">
        <a href="#" className="text-white mx-2"><i className="fab fa-instagram"></i></a>
        <a href="#" className="text-white mx-2"><i className="fab fa-twitter"></i></a>
        <a href="#" className="text-white mx-2"><i className="fab fa-linkedin-in"></i></a>
      </div>
    </div>
  </footer>
);

// --- MAIN APP COMPONENT ---
function App() {
  // 'view' state controls what the user sees: 'home' or 'login'
  const [view, setView] = useState('home');

  useEffect(() => {
    // Only run smooth scroll logic if we are on the home view
    if (view === 'home') {
      document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function (e) {
          e.preventDefault();
          const target = document.querySelector(this.getAttribute('href'));
          if (target) {
              target.scrollIntoView({ behavior: 'smooth' });
          }
        });
      });
    }
  }, [view]); // Re-run this when 'view' changes

  return (
    <div className="App">
      {/* Pass view and setView to Navbar so it knows which button to show */}
      <Navbar currentView={view} setView={setView} />

      {/* CONDITIONAL RENDERING: The "Remote Control" Logic */}
      {view === 'home' ? (
        <>
          <Hero />
          <About />
          <Services />
          <Portfolio />
          <Contact />
        </>
      ) : (
        <LoginPage onBack={() => setView('home')} />
      )}

      <Footer />
    </div>
  );
}

export default App;
